mapper.select:
select  IFNULL(count(*),0)as certified,county as name  from cms_jg_dispute 

	 		city_code=#{address,jdbcType=OTHER}

	 			and year(arose_date)>=#{year,jdbcType=OTHER} and year(arose_date)<=#{year2,jdbcType=OTHER}


	 			and year(arose_date)=#{year,jdbcType=OTHER} and month(arose_date)>=#{month,jdbcType=OTHER} and month(arose_date)<=#{month2,jdbcType=OTHER}


	 			and year(arose_date)=#{year,jdbcType=OTHER} and month(arose_date)=#{month,jdbcType=OTHER} and day(arose_date)=#{day,jdbcType=OTHER}

	 		and identify_status=#{status,jdbcType=OTHER}

	 group by county_code

  		select 

    	from cms_jg_dispute

		 		city_code=#{address,jdbcType=OTHER}

		 			and year(arose_date)>=#{year,jdbcType=OTHER} and year(arose_date)<=#{year2,jdbcType=OTHER}


		 			and year(arose_date)=#{year,jdbcType=OTHER} and month(arose_date)>=#{month,jdbcType=OTHER} and month(arose_date)<=#{month2,jdbcType=OTHER}


		 			and year(arose_date)=#{year,jdbcType=OTHER} and month(arose_date)=#{month,jdbcType=OTHER} and day(arose_date)=#{day,jdbcType=OTHER}


		order by booked_date

  		select s.id as id ,s.org_id orgId   
  		from  cms_jg_dispute s 

				 s.org_id in

		            #{orgId}


		        and    s.booked_date >=#{beginDate,jdbcType=OTHER} 
		        and s.booked_date <=#{endDate,jdbcType=OTHER}

select a.ord_id as id,a.org_id as orgId,a.doctor_id as doctorId,e.level as level from cs_outp_order a left join cs_outp_order_dtl b on(a.ord_id=b.ord_id) left join ta_pha_drug_info c on(b.ord_item_id=c.pha_drug_id) left join ta_pha_dict_drug_info d on(c.drug_trade_id=d.drug_trade_id) left join ta_pha_dict_drug_antibiotic_level e on(d.drug_comm_id=e.drug_common_id)
a.org_id in
#{orgId}
and a.order_date >=#{beginDate,jdbcType=OTHER} and a.order_date <=#{endDate,jdbcType=OTHER} and b.ord_type='20107-0001' and e.antibiotic_level_polity_id=#{antibioticId,jdbcType=OTHER}

select s.arose_date as date ,'医疗争议' as type ,s.org_name as name,s.org_id as orgId,s.user_id as doctorId from cms_jg_dispute s where s.id =#{strId,jdbcType=OTHER}
union
select b.visit_date as date,'传染病' as type,a.icd_name as name,b.org_id as orgId,b.doctor_id as doctorId from cs_outp_visit_dx a left join cs_outp_visit b on(a.visti_id=b.visti_id) where a.dx_id =#{strId,jdbcType=OTHER}
union
select a.order_date as date,'抗生素' as type,d.comm_name as name,a.org_id as orgId,a.doctor_id as doctorId from cs_outp_order a left join cs_outp_order_dtl b on(a.ord_id=b.ord_id) left join ta_pha_drug_info c on(b.ord_item_id=c.pha_drug_id) left join ta_pha_dict_drug_info d on(c.drug_trade_id=d.drug_trade_id) where a.ord_id=#{strId,jdbcType=OTHER}
